sys_variable_value.value:
(function execute(inputs, outputs) {
    // Query question_answer for matching table_name and table_sys_id
    let questionAnswerGr = new GlideRecordSecure('question_answer');
    if(inputs.table_name) {
        questionAnswerGr.addQuery('table_name', inputs.table_name);
    }
    questionAnswerGr.addQuery('table_sys_id', inputs.sysId);
    questionAnswerGr.setLimit(100);
    questionAnswerGr.query();

    // Error handling: check for valid query execution
    if (!questionAnswerGr.isValid()) {
        gs.logError('question_answer table is not valid or accessible');
        outputs.error = 'Table not accessible';
        return;
    }

    // Collect matching records
    outputs.answers = [];
    while (questionAnswerGr.next()) {
        outputs.answers.push({
            question_text: questionAnswerGr.question.question_text,
            question_name: questionAnswerGr.question.name,
            answer: questionAnswerGr.getValue('value')
        });
    }
})(inputs, outputs);

